test="""...........
.....###.#.
.###.##..#.
..#.#...#..
....#.#....
.##..S####.
.##..#...#.
.......##..
.##.#.####.
.##..##.##.
..........."""
a=test
a.replace
b=[x for x in a.splitlines()]
w,h = len(b[0]),len(b)
start = (w//2,h//2)
edgeCoords = [(0,y) for y in range(h)]+[(w-1,y) for y in range(h)]+[(x,0) for x in range(w)]+[(x,h-1) for x in range(w)]
edgeCoords = {coord:None for coord in edgeCoords}

def traverse(start):
    dirs=[[-1,0],[0,-1],[1,0],[0,1]]
    queue=[[start,0]]
    visited = {start}
    visitedSteps = {0:{start}}
    targetSteps = 6
    while queue:
        pos, steps = queue.pop(0)
        x,y = pos
        if steps >= targetSteps:
            break
        for dx,dy in dirs:
            nx,ny = x+dx,y+dy
            if nx < 0 or ny < 0 or nx >= w or ny >= h:
                continue
            if b[ny][nx] == "#":
                continue
            if (nx,ny) in visited:
                continue
            visited.add((nx,ny))
            visitedSteps.setdefault(steps+1,set()).add((nx,ny))
            queue.append([(nx,ny), steps+1])
    return visitedSteps

visitedSteps = traverse(start)
total = 0
for x in visitedSteps:
    if x%2==0:
        total += len(visitedSteps[x])
print(total)